css:
@import url('https://fonts.googleapis.com/css2?family=Almarai&display=swap');

* {
    font-family: 'Almarai', sans-serif!important;
}

@page { 
    size: A5 landscape ;
}
.en {
    direction: ltr;
}
.ar {
    direction: rtl;
}
.parent {
    display : flex;
    justify-content : space-between ;
    position:relative;
}

.parent > div {
    width: 48%;
    /*border: 1px solid;*/
}
.parent > p:first-child , .parent > p:last-child {
        /*border: 1px solid;*/
        width : 30%;

}

.parent > p:nth-child(2) {
    width: 40%;
    text-align : center ;
}
.print-format p {
    font-size : 12px!important;
    font-weight:bold!important;
    padding: 0px 3px;
}

.parent > div:first-child::after {
    content : "";
    position: absolute;
    height: calc(100% + 50px);
    left: calc(50% - 0.5px);
    top:0;
    border: 1px dotted black

}

.grid-container {
    display: inline-grid;
    grid-template-columns: repeat(4, 25%);
    padding: 10px;
    margin: 0 auto; /* Center the grid container on the page */
}

.grid-item {
    padding: 20px;
    font-size: 16px;
    text-align: center;
}
.custom-label{
  font-weight: bold;
  color:#4da7db;   
  }
table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 20px;
}

th, td {
    border: 1px solid #ddd;
    padding: 8px;
    text-align: left;
}

th {
     color:#4da7db !important;
     font-weight:bold !important;
}
table tr:nth-child(even) {
    background-color: #4da7db;
}
